// Core
import React, { Component } from 'react';
import { render } from 'react-dom';
import { Provider, connect } from 'react-redux';
import { IntlProvider, FormattedMessage, addLocaleData } from 'react-intl';
import en from 'react-intl/locale-data/en';
import uk from 'react-intl/locale-data/uk';
import ru from 'react-intl/locale-data/ru';

addLocaleData([ ...uk, ...ru, ...en ]);

// Instruments
import { store } from './init/store';
import { setLocale } from './bus/intl/actions';

@connect(
    (state) => state.intl,
    { setLocale },
)
class Parent extends Component {
    _setLocaleUk = () => {
        this.props.setLocale('uk');
    };

    _setLocaleRu = () => {
        this.props.setLocale('ru');
    };

    _setLocaleEn = () => {
        this.props.setLocale('en');
    };

    render() {
        const { locale } = this.props;

        let flag = '🇺🇸';

        if (locale === 'uk') {
            flag = '🇺🇦';
        } else if (locale === 'ru') {
            flag = '🇷🇺';
        }

        return (
            <section className = 'example'>
                <FormattedMessage
                    defaultMessage = '🌎 Наразi вибрана локаль: 🇺🇦'
                    description = 'Выбранная локаль'
                    id = 'message-1'
                    tagName = 'h4'
                    values = {{
                        locale: flag,
                    }}
                />
                <div>
                    <button onClick = { this._setLocaleUk }>🇺🇦</button>
                    <button onClick = { this._setLocaleRu }>🇷🇺</button>
                    <button onClick = { this._setLocaleEn }>🇺🇸</button>
                </div>
                <FormattedMessage
                    defaultMessage = '👋🏼 Здоровенькi були, гостю.'
                    description = 'Приветствие пользователя'
                    id = 'message-2'
                    tagName = 'h1'
                    values = {{
                        enName: 'Walter',
                        ukName: 'Волтере',
                        ruName: 'Уолтер',
                    }}
                />
                <FormattedMessage
                    defaultMessage = '🍻 Ласкаво просимо до заходу.'
                    description = 'Приглашение пользователя'
                    id = 'message-3'
                />
            </section>
        );
    }
}

const ConnectedIntlProvider = connect((state) => state.intl)(
    class extends Component {
        render() {
            return (
                <IntlProvider
                    locale = { this.props.locale }
                    messages = { this.props.messages }>
                    <Parent />
                </IntlProvider>
            );
        }
    },
);

render(
    <Provider store = { store }>
        <ConnectedIntlProvider>
            <Parent />
        </ConnectedIntlProvider>
    </Provider>,
    document.getElementById('app'),
);
