// Core
import React, { Component } from 'react';
import { render } from 'react-dom';
import { I18n, Translate } from 'react-i18nify';

I18n.setTranslations({
    en: {
        application: {
            title: '🎉 Welcome to amazing app with i18n, yeey!',
        },
        export:   'Export %{count} items',
        export_0: 'Nothing to export',
        export_1: 'Export %{count} item',
    },
    ru: {
        application: {
            title:
                '🎉 Добро пожаловать в невероятное приложение с i18n, ййуху!',
        },
        export:   'Экспортируются %{count} единиц',
        export_0: 'Ничего не экспортируется',
        export_1: 'Экспортируется %{count} единица',
    },
});
I18n.setLocale('ru');

class Parent extends Component {
    state = {
        count: 0,
    };

    changeLocaleRu = () => {
        I18n.setLocale('ru');
    };

    changeLocaleEn = () => {
        I18n.setLocale('en');
    };

    increaseExport = () => {
        this.setState(({ count: oldCount }) => ({
            count: oldCount + 1,
        }));
    };

    decreaseExport = () => {
        this.setState(({ count: oldCount }) => ({
            count: oldCount - 1,
        }));
    };

    render() {
        const { count } = this.state;

        return (
            <section className = 'example'>
                <Translate
                    tag = 'h1'
                    value = 'application.title'
                />
                <div>
                    <button onClick = { this.changeLocaleRu }>RU</button>
                    <button onClick = { this.changeLocaleEn }>EN</button>
                </div>
                <div>
                    <button onClick = { this.increaseExport }>+</button>
                    <button onClick = { this.decreaseExport }>-</button>
                </div>
                <Translate
                    count = { count }
                    value = 'export'
                />
            </section>
        );
    }
}

render(<Parent />, document.getElementById('app'));
