// Core
import React, { Component } from 'react';
import { render } from 'react-dom';
import { I18n, Translate } from 'react-i18nify';

I18n.setTranslations({
    en: {
        application: {
            title: 'ðŸŽ‰ Welcome to amazing app with i18n, yeey!',
        },
        export:   'Export %{count} items',
        export_0: 'Nothing to export',
        export_1: 'Export %{count} item',
    },
    ru: {
        application: {
            title:
                'ðŸŽ‰ Ð”Ð¾Ð±Ñ€Ð¾ Ð¿Ð¾Ð¶Ð°Ð»Ð¾Ð²Ð°Ñ‚ÑŒ Ð² Ð½ÐµÐ²ÐµÑ€Ð¾ÑÑ‚Ð½Ð¾Ðµ Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ðµ Ñ i18n, Ð¹Ð¹ÑƒÑ…Ñƒ!',
        },
        export:   'Ð­ÐºÑÐ¿Ð¾Ñ€Ñ‚Ð¸Ñ€ÑƒÑŽÑ‚ÑÑ %{count} ÐµÐ´Ð¸Ð½Ð¸Ñ†',
        export_0: 'ÐÐ¸Ñ‡ÐµÐ³Ð¾ Ð½Ðµ ÑÐºÑÐ¿Ð¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÑ‚ÑÑ',
        export_1: 'Ð­ÐºÑÐ¿Ð¾Ñ€Ñ‚Ð¸Ñ€ÑƒÐµÑ‚ÑÑ %{count} ÐµÐ´Ð¸Ð½Ð¸Ñ†Ð°',
    },
});
I18n.setLocale('ru');

class Parent extends Component {
    state = {
        count: 0,
    };

    changeLocaleRu = () => {
        I18n.setLocale('ru');
    };

    changeLocaleEn = () => {
        I18n.setLocale('en');
    };

    increaseExport = () => {
        this.setState(({ count: oldCount }) => ({
            count: oldCount + 1,
        }));
    };

    decreaseExport = () => {
        this.setState(({ count: oldCount }) => ({
            count: oldCount - 1,
        }));
    };

    render() {
        const { count } = this.state;

        return (
            <section className = 'example'>
                <Translate
                    tag = 'h1'
                    value = 'application.title'
                />
                <div>
                    <button onClick = { this.changeLocaleRu }>RU</button>
                    <button onClick = { this.changeLocaleEn }>EN</button>
                </div>
                <div>
                    <button onClick = { this.increaseExport }>+</button>
                    <button onClick = { this.decreaseExport }>-</button>
                </div>
                <Translate
                    count = { count }
                    value = 'export'
                />
            </section>
        );
    }
}

render(<Parent />, document.getElementById('app'));
