// Core
import React, { Component } from 'react';

// Instruments
import { getDisplayName } from '../../../../helpers';

/**
 * function (options) {
 *     return function (WrappedComponent) {
 *         return withState(options, WrappedComponent)
 *     }
 * }
 */

export const withState = ({
    stateName,
    stateValue,
    stateUpdaterName,
    stateUpdater,
}) => (Enhanceable) => {
    class WithState extends Component {
        state = {
            [ stateName ]: stateValue,
        };

        [stateUpdaterName] = () => {
            this.setState(stateUpdater);
        };

        render() {
            const updatersToForward = {
                [ stateUpdaterName ]: this[ stateUpdaterName ],
            };

            return (
                <Enhanceable
                    { ...this.props }
                    { ...this.state }
                    { ...updatersToForward }
                />
            );
        }
    }

    WithState.displayName = `withState(${getDisplayName(Enhanceable)})`;

    return WithState;
};
